mapper.insert:
INSERT INTO categories (
    code,   
    name,
    parent_category_id
) VALUES (
    #{code},    
    #{name},
    #{parentCategoryId}
)

mapper.update:
UPDATE categories
SET
    code = #{code},
    name = #{name},
    parent_category_id = #{parentCategoryId}
WHERE category_id = #{categoryId}

mapper.select:
WITH RECURSIVE category_path AS ( SELECT category_id as categoryId, code, name, parent_category_id as parentCategoryId, 0 as depth FROM categories WHERE category_id = #{categoryId}
UNION ALL
SELECT c.category_id as categoryId, c.code, c.name, c.parent_category_id as parentCategoryId, cp.depth + 1 as depth FROM categories c INNER JOIN category_path cp ON c.category_id = cp.parentCategoryId ) SELECT categoryId, code, name, parentCategoryId FROM category_path ORDER BY depth DESC
